try {
  // Extract symbols and scores from webhook trigger
  const input = $input.first();
  const data = input ? input.json : {};

  console.log('Webhook data received:', JSON.stringify(data, null, 2));

  const symbols = data.symbols || [];
  const scores = data.metadata?.opportunityScores || [];
  const priority = data.priority || 'normal';
  const source = data.metadata?.source || 'manual';

  if (symbols.length === 0) {
    console.warn('No symbols received for analysis');
    return [{
      json: {
        error: 'No symbols provided',
        batches: [],
        summary: { totalSymbols: 0, batchCount: 0 }
      }
    }];
  }

  console.log(`Processing ${symbols.length} symbols from ${source}`);

  // Create priority queue with safety checks
  const priorityQueue = symbols.map((symbol, index) => {
    const scoreData = scores.find(s => s && s.symbol === symbol) || {};
    return {
      symbol: symbol,
      opportunityScore: scoreData.score || 50,
      priority: priority,
      source: source,
      index: index
    };
  }).filter(item => item.symbol); // Remove any null/undefined symbols

  // Sort by opportunity score (highest first)
  priorityQueue.sort((a, b) => (b.opportunityScore || 0) - (a.opportunityScore || 0));

  // Split into batches for parallel processing
  const batchSize = Math.min(10, Math.max(1, Math.ceil(priorityQueue.length / 5))); // Dynamic batch size
  const batches = [];

  for (let i = 0; i < priorityQueue.length; i += batchSize) {
    const batchSymbols = priorityQueue.slice(i, i + batchSize);
    batches.push({
      batchNumber: Math.floor(i / batchSize) + 1,
      symbols: batchSymbols,
      totalSymbols: batchSymbols.length,
      avgScore: batchSymbols.reduce((sum, s) => sum + (s.opportunityScore || 0), 0) / batchSymbols.length
    });
  }

  console.log(`Created ${batches.length} batches with sizes:`, batches.map(b => b.totalSymbols));

  return batches.map(batch => ({ json: batch }));

} catch (error) {
  console.error('Error preparing batches:', error.message);

  return [{
    json: {
      error: error.message,
      batches: [],
      summary: { 
        totalSymbols: 0, 
        batchCount: 0,
        errorDetails: error.stack
      }
    }
  }];
}

