try {
  // Get market conditions from previous node
  const input = $input.first();
  const marketData = input ? input.json : {};

  // Mock VIX data if not available (for testing)
  const mockVix = Math.random() * 30 + 15; // Random VIX between 15-45
  const mockVolume = 50000000 + Math.random() * 50000000; // 50-100M volume

  const marketConditions = {
    vix: mockVix,
    totalVolume: mockVolume,
    trend: mockVix > 25 ? 'risk-off' : 'neutral'
  };

  // Determine adaptive filters based on market conditions
  const adaptiveFilters = {
    exchange: 'US',
    minVolume: marketConditions.vix > 25 ? 2000000 : 1000000,
    minPrice: 5,
    maxPrice: marketConditions.totalVolume < 50000000 ? 200 : 500,
    minDailyChange: marketConditions.vix > 30 ? 3 : 2,
    excludeSectors: ['REIT', 'ADR'],
    limit: marketConditions.vix > 25 ? 50 : 30
  };

  // Time-based adjustments
  const hour = new Date().getHours();
  let timeOfDay = 'midday';

  if (hour === 8) timeOfDay = 'preMarket';
  else if (hour === 9) timeOfDay = 'openingBell';
  else if (hour === 15) timeOfDay = 'powerHour';
  else if (hour >= 16) timeOfDay = 'afterHours';

  if (timeOfDay === 'powerHour') {
    adaptiveFilters.minDailyChange = 2.5;
    adaptiveFilters.limit = 35;
  }

  // Log the configuration
  console.log(`Market conditions: VIX=${marketConditions.vix.toFixed(1)}, Volume=${marketConditions.totalVolume}`);
  console.log(`Time of day: ${timeOfDay}`);
  console.log(`Adaptive filters:`, JSON.stringify(adaptiveFilters, null, 2));

  return [{
    json: {
      filters: adaptiveFilters,
      marketConditions: marketConditions,
      timeOfDay: timeOfDay,
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  console.error('Error in adaptive filters:', error.message);

  // Return default filters on error
  return [{
    json: {
      filters: {
        exchange: 'US',
        minVolume: 1000000,
        minPrice: 5,
        maxPrice: 200,
        minDailyChange: 2,
        excludeSectors: ['REIT', 'ADR'],
        limit: 10
      },
      marketConditions: { vix: 20, totalVolume: 50000000, trend: 'neutral' },
      timeOfDay: 'midday',
      timestamp: new Date().toISOString(),
      error: error.message
    }
  }];
}

try {
  // Get scan result from previous node
  const input = $input.first();
  const scanResult = input ? input.json : {};
  const candidates = scanResult.candidates || [];

  console.log(`Starting quality control on ${candidates.length} candidates`);

  // Filter out suspicious results with comprehensive checks
  const validCandidates = candidates.filter(candidate => {
    try {
      // Ensure candidate has required fields
      if (!candidate.symbol || !candidate.price || candidate.volume === undefined) {
        console.warn(`Missing required fields for candidate:`, candidate.symbol);
        return false;
      }

      // Check for unrealistic gains (more than 100%)
      const changePercent = Math.abs(candidate.changePercent || 0);
      if (changePercent > 100) {
        console.warn(`Unrealistic change: ${candidate.symbol} with ${changePercent}% change`);
        return false;
      }

      // Check for minimum liquidity
      if (candidate.volume < 10000) {
        console.warn(`Low liquidity: ${candidate.symbol} with volume ${candidate.volume}`);
        return false;
      }

      // Check for penny stocks
      if (candidate.price < 1) {
        console.warn(`Penny stock filtered: ${candidate.symbol} at $${candidate.price}`);
        return false;
      }

      // Check for suspicious volume spikes
      const volumeRatio = candidate.volumeRatio || 1;
      if (volumeRatio > 50) {
        console.warn(`Suspicious volume spike: ${candidate.symbol} with ${volumeRatio}x normal volume`);
        return false;
      }

      // Check for valid price range
      if (candidate.price > 10000) {
        console.warn(`Unrealistic price: ${candidate.symbol} at $${candidate.price}`);
        return false;
      }

      return true;

    } catch (error) {
      console.error(`Error checking candidate ${candidate.symbol}:`, error.message);
      return false;
    }
  });

  const qualityControl = {
    original: candidates.length,
    filtered: validCandidates.length,
    removed: candidates.length - validCandidates.length,
    filterRules: {
      unrealisticChange: '> 100%',
      lowLiquidity: '< 10,000 volume',
      pennyStocks: '< $1',
      volumeSpikes: '> 50x normal',
      priceRange: '> $10,000'
    }
  };

  console.log(`Quality control complete: ${qualityControl.original} -> ${qualityControl.filtered} (removed ${qualityControl.removed})`);

  return [{
    json: {
      ...scanResult,
      candidates: validCandidates,
      qualityControl: qualityControl,
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  console.error('Error in quality control:', error.message);

  // Return original data on error
  const input = $input.first();
  return [{
    json: {
      ...input.json,
      qualityControl: {
        original: 0,
        filtered: 0,
        removed: 0,
        error: error.message
      },
      timestamp: new Date().toISOString()
    }
  }];
}
